2ÔºâÈÄöËøáÈÄªËæë‰ΩøÁî®

 * AppStorage.Get(key) Ëé∑ÂèñÊï∞ÊçÆ
 * AppStorage.Set(key,value) Ë¶ÜÁõñÊï∞ÊçÆ
 * const link: SubscribedAbstractProperty = AppStorage.Link(key) Ë¶ÜÁõñÊï∞ÊçÆ
   * link.set(value) ‰øÆÊîπ
   * link.get() Ëé∑Âèñ




AppStorage.SetOrCreate('user', { name: 'jack', age: 18 })

@Entry
@Component
struct Index {
  @StorageLink('user')
  user: User = {}

  build() {
    Column({ space: 15 }) {
      Text('IndexÔºö')
      Text(this.user.name + this.user.age)
        .onClick(() => {
          this.user.age++
        })
      Divider()
      Text('Get()')
        .onClick(() => {
          // ‰ªÖËé∑Âèñ
          const user = AppStorage.Get('user')
          promptAction.showToast({
            message: JSON.stringify(user)
          })
        })
      Text('Set()')
        .onClick(() => {
          // Áõ¥Êé•ËÆæÁΩÆ
          AppStorage.Set('user', {
            name: 'tom',
            age: 100
          })
          // ËßÇÂØüÈ°µÈù¢Êõ¥Êñ∞Ê≤°
        })
      Text('Link()')
        .onClick(() => {
          // Ëé∑ÂèñuserÁöÑprop
          const user: SubscribedAbstractProperty = AppStorage.Link('user')
          user.set({
            name: user.get().name,
            // Ëé∑ÂèñÂêé‰øÆÊîπ
            age: user.get().age + 1
          })
        })
    }
    .width('100%')
    .height('100%')
    .justifyContent(FlexAlign.Center)
  }
}



