2ÔºâÂ§çÊùÇÊï∞ÊçÆÁ±ªÂûãÁöÑÂêÉÂ∞±Â•ΩÔºåÂíåËé∑ÂèñÂíå‰øÆÊîπ




PersistentStorage.PersistProp('userJson', `{ "name": "jack", "age": 18 }`)

@Entry
@Component
struct Index {
  @StorageProp('userJson')
  @Watch('onUpdateUser')
  userJson: string = '{}'
  @State
  user: User = JSON.parse(this.userJson)

  onUpdateUser() {
    this.user = JSON.parse(this.userJson)
  }

  build() {
    Column({ space: 15 }) {
      Text('IndexÔºö')
      Text(this.user.name + this.user.age)
        .onClick(() => {
          this.user.age++
          // ‰øÆÊîπ
          AppStorage.Set('userJson', JSON.stringify(this.user))
        })
      Divider()
      Text('Get()')
        .onClick(() => {
          // Ëé∑Âèñ
          const user = AppStorage.Get('userJson')
          promptAction.showToast({ message: user })
        })
    }
    .width('100%')
    .height('100%')
    .justifyContent(FlexAlign.Center)
  }
}



