onLike(item: ReplyItem) {
  const reply = { ...item }
  if (reply.likeFlag) {
    reply.likeNum--
    reply.likeFlag = false
    promptAction.showToast({ message: 'å–æ¶ˆç‚¹èµ' })
  } else {
    reply.likeNum++
    reply.likeFlag = true
    promptAction.showToast({ message: 'ç‚¹èµæˆåŠŸ' })
  }
  const index = this.replyList.findIndex(rep => rep.id === reply.id)
  this.replyList.splice(index, 1, reply)
}


