 * 封装等比例缩放函数




const designWidth = 375
const devicePhysics = display.getDefaultDisplaySync().width

export const vp2vp = (originSize: number) => {
  // 不是PAD是手机等比例缩放
  if ( deviceInfo.deviceType !== 'tablet' ) {
    return px2vp(devicePhysics) / designWidth * originSize
  }
  return originSize
}



